class Geografico{constructor(){window.geoApiRoot="https://psweb4-geo.azurewebsites.net/api/",$.ajax({url:window.geoApiRoot+"sys/version"}).done((function(o){window.geoApiVersion=o,console.log("LNC-GEO.JS v."+window.geoApiVersion)}))}getCurrentVersion(){return window.geoApiVersion}listAllPaises(){return $.ajax({url:window.geoApiRoot+"paises/all"})}listPaisesConEstados(){return $.ajax({url:window.geoApiRoot+"paises/detailed"})}getPais(o){return $.ajax({url:window.geoApiRoot+"paises/view/"+o})}listEstadosByPais(o){return $.ajax({url:window.geoApiRoot+"estados/bypais/"+o})}getEstado(o){return $.ajax({url:window.geoApiRoot+"estados/view/"+o})}listMunicipiosByEstado(o,i){return $.ajax({url:window.geoApiRoot+"municipios/byestado/"+o+"-"+i})}getMunicipio(o){return $.ajax({url:window.geoApiRoot+"municipios/view/"+o})}listCiudadesByMunicipio(o,i,e){return $.ajax({url:window.geoApiRoot+"ciudades/bymunicipio/"+o+"-"+i+"-"+e})}getCiudad(o){return $.ajax({url:window.geoApiRoot+"ciudades/view/"+o})}}const geografico=new Geografico;