class Geografico{constructor(){window.geoApiRoot="https://ctc-geografico.psweb.me/",$.ajax({url:window.geoApiRoot+"sys/version"}).done((function(o){window.geoApiVersion=o,console.log("LNC-GEO.JS v."+window.geoApiVersion)}))}getCurrentVersion(){return window.geoApiVersion}listAllPaises(){return $.ajax({url:window.geoApiRoot+"paises/list"})}getPais(o){return $.ajax({url:window.geoApiRoot+"paises/get/"+o})}listEstadosByPais(o){return $.ajax({url:window.geoApiRoot+"estados/list/"+o})}getEstado(o){return $.ajax({url:window.geoApiRoot+"estados/get/"+o})}listMunicipiosByEstado(o,i){return $.ajax({url:window.geoApiRoot+"municipios/list/"+o+"/"+i})}getMunicipio(o){return $.ajax({url:window.geoApiRoot+"municipios/get/"+o})}listCiudadesByMunicipio(o,i,e){return $.ajax({url:window.geoApiRoot+"ciudades/list/"+o+"/"+i+"/"+e})}getCiudad(o){return $.ajax({url:window.geoApiRoot+"ciudades/get/"+o})}}const geografico=new Geografico;